function connectWebViewJavascriptBridge(callback){if(window.WebViewJavascriptBridge){callback(WebViewJavascriptBridge);}else{document.addEventListener("WebViewJavascriptBridgeReady",function(){callback(WebViewJavascriptBridge);},false);}}function showNativeSpinner_iOS(){var task=function(bridge){bridge.send("showNativeSpinner");};connectWebViewJavascriptBridge(task);}function hideNativeSpinner_iOS(){var task=function(bridge){bridge.send("hideNativeSpinner");};connectWebViewJavascriptBridge(task);}function triggerGeolocationUpdate_iOS(){var task=function(bridge){bridge.send("getLocation");};connectWebViewJavascriptBridge(task);}